mapper.select:
select c.carpool_no, c.driver_no, m.member_name, m.member_gender, m.member_phone, m.member_email, c.departure_date, c.capacity, c.closure, (select count(*) from passenger p where p.carpool_no = c.carpool_no and match_status=2) as reserved from carpool c join member_tbl m on (c.driver_no = m.member_no)
where driver_no=#{driverNo}
where member_no=#{memberNo}

mapper.update:
update passenger
set match_status=1 set match_status=2
where match_no=#{matchNo}

update carpool
set closure=1 set closure=3
where carpool_no=#{carpoolNo}

mapper.select:
select match_no as matchNo, 
match_status as matchStatus
from passenger where carpool_no=#{carpoolNo} and passenger_no=#{memberNo}
